mapper.select:
SELECT

FROM suspension_record
WHERE suspensionId = #{suspensionId, jdbcType=INTEGER}

mapper.update:
UPDATE suspension_record
userId = #{userid, jdbcType=INTEGER}, borrowId = #{borrowid, jdbcType=INTEGER}, startDate = #{startDate, jdbcType=TIMESTAMP}, endDate = #{endDate, jdbcType=TIMESTAMP}, suspensionReason = #{suspensionReason, jdbcType=VARCHAR}, borrowingPermission = #{borrowingPermission, jdbcType=TINYINT},
WHERE suspensionId = #{suspensionid, jdbcType=INTEGER}
